import React, { useEffect, useState } from 'react';

const App = () => {
  const [hr, setHr] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('http://localhost:5000/furthest-homer')
      .then(res => res.json())
      .then(data => {
        setHr(data);
        setLoading(false);
      })
      .catch(err => {
        console.error(err);
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Loading...</p>;
  if (!hr) return <p>No home runs found yesterday.</p>;

  return (
    <div className="max-w-md p-4 rounded-xl shadow-md border">
      <h2 className="text-xl font-semibold mb-2">Furthest Home Run (Yesterday)</h2>
      <p><strong>Player:</strong> {hr.player}</p>
      <p><strong>Distance:</strong> {hr.distance} ft</p>
      <p><strong>Game ID:</strong> {hr.game}</p>
    </div>
  );
};

export default App;
