import React, { useEffect, useState } from 'react';

type Homerun = {
  player: string;
  distance: number;
  game: string;
};

const App = () => {
  const [hr, setHr] = useState<Homerun | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('http://localhost:5000/furthest-homer')
      .then(res => res.json())
      .then(data => {
        setHr(data);
        setLoading(false);
      })
      .catch(err => {
        console.error(err);
        setLoading(false);
      });
  }, []);

  if (loading) return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-br from-green-200 to-green-500">
      <p className="text-xl font-semibold text-white">Loading...</p>
    </div>
  );
  if (!hr) return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-br from-green-200 to-green-500">
      <p className="text-xl font-semibold text-white">No home runs found yesterday.</p>
    </div>
  );

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-br from-green-200 to-green-500">
      <div className="max-w-md w-full p-8 rounded-2xl shadow-2xl border-4 border-brown-700 bg-white bg-opacity-90 text-center">
        <h2 className="text-2xl font-extrabold mb-4 text-green-900 tracking-wide">Furthest Home Run (Yesterday)</h2>
        <div className="mb-4">
          <p className="text-lg font-medium text-brown-800"><span className="font-bold text-green-700">Player:</span> {hr.player}</p>
          <p className="text-lg font-medium text-brown-800"><span className="font-bold text-green-700">Distance:</span> {hr.distance} ft</p>
          <p className="text-lg font-medium text-brown-800"><span className="font-bold text-green-700">Game ID:</span> {hr.game}</p>
        </div>
        <div className="mt-6 flex justify-center">
          <svg width="60" height="60" viewBox="0 0 60 60" fill="none">
            <circle cx="30" cy="30" r="28" stroke="#B45309" strokeWidth="4" fill="#F9FAFB"/>
            <path d="M15 30 Q30 10 45 30" stroke="#047857" strokeWidth="3" fill="none"/>
            <circle cx="30" cy="30" r="8" fill="#B91C1C" />
          </svg>
        </div>
      </div>
    </div>
  );
};

export default App;
