import React, { useEffect, useState } from 'react';

type Homerun = {
  player: string;
  distance: number;
  game: string;
};

const App = () => {
  const [hr, setHr] = useState<Homerun | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('http://localhost:5000/furthest-homer')
      .then(res => res.json())
      .then(data => {
        setHr(data);
        setLoading(false);
      })
      .catch(err => {
        console.error(err);
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Loading...</p>;
  if (!hr) return <p>No home runs found yesterday.</p>;

  return (
  <div
    style={{
      maxWidth: 400,
      margin: "60px auto",
      padding: 24,
      borderRadius: 24,
      boxShadow: "0 8px 32px rgba(0,0,0,0.15)",
      border: "3px solid #7c4700",
      background: "linear-gradient(135deg, #b7e4c7 0%, #40916c 100%)",
      color: "#1b4332",
      textAlign: "center",
    }}
  >
    <h2 style={{ fontSize: 28, fontWeight: 700, marginBottom: 16 }}>
      Furthest Home Run (Yesterday)
    </h2>
    <p>
      <strong>Player:</strong> {hr.player}
    </p>
    <p>
      <strong>Distance:</strong> {hr.distance} ft
    </p>
  </div>
);
};

export default App;
